fun main(args: Array<String>) {
    var sum = 0

    val list = mutableListOf<Int>()

    var workingSum = 0
    input.mapIndexed { index, char ->
        when (char) {
            ')' -> -1
            '(' -> 1
            else -> 0
        }.also {
            workingSum += it
            if (workingSum == -1) {
                println("-1 at $index")
            }
        }

    }.sum().also { println(it) }

}

